import{_ as n,r as o,o as s,c as r,a as e,b as a,d as c,e as i}from"./app-aa9cafec.js";const l="/MLblogs/assets/2024-02-20-20-13-37-image-48817845.png",_={},h={class:"custom-container info"},d=e("svg",{xmlns:"http://www.w3.org/2000/svg","xmlns:xlink":"http://www.w3.org/1999/xlink",viewBox:"0 0 24 24"},[e("g",{fill:"none",stroke:"currentColor","stroke-width":"2","stroke-linecap":"round","stroke-linejoin":"round"},[e("circle",{cx:"12",cy:"12",r:"9"}),e("path",{d:"M12 8h.01"}),e("path",{d:"M11 12h1v4h1"})])],-1),p=e("p",{class:"custom-container-title"},"INFO",-1),m={href:"https://zhuanlan.zhihu.com/p/26945814",target:"_blank",rel:"noopener noreferrer"},u=e("p",null,"维度灾难指的是：",-1),g=e("p",null,"随着维度增加，分类器性能提升；维度增加到某值后，分类器性能下降",-1),x=i('<h2 id="特征稀疏性" tabindex="-1"><a class="header-anchor" href="#特征稀疏性" aria-hidden="true">#</a> 特征稀疏性</h2><p>比如猫狗二分类问题中，假设有10个样本，每一个特征空间的宽度=5：</p><p>1D空间中，10个训练样本完全覆盖了1D特征空间，1D下的样本密度是10/5=2。在2D空间中，同样是10个训练样本，它构成的2D特征空间面积为5x5=25. 因此，2D下的样本密度是10/25=0.4。在3D空间中，10个训练样本构成的特征空间大小为5x5x5=125，因此，3D下的样本密度为10/125=0.08。</p><p><strong>继续增加特征，整个特征空间维度增加，并变得越来越稀疏</strong>。由于稀疏性，我们<strong>更加容易找到一个超平面来实现分类</strong>。这是因为随着特征数量变得无限大，<strong>训练样本在最佳超平面的错误侧的可能性将会变得无限小</strong>。然而，如果我们将高维的分类结果投影到低维空间中，将会出现一个严重的问题，即过拟合：</p><img src="'+l+'" title="" alt="" data-align="center"><p>分类器学习了训练数据的噪声和异常，而对样本外的数据拟合效果并不理想，甚至很差。</p>',6);function f(k,w){const t=o("ExternalLinkIcon");return s(),r("div",null,[e("div",h,[d,p,e("p",null,[e("a",m,[a("机器学习中的维度灾难 - 知乎 (zhihu.com)"),c(t)])]),u,g]),x])}const v=n(_,[["render",f],["__file","Dimension_Curse.html.vue"]]);export{v as default};
